class Solution:
    def numMusicPlaylists(self, N: int, L: int, K: int) -> int:
        MOD = 10**9 + 7

        dp = {}

        def solve(length, played):
            if (length, played) in dp:
                return dp[(length, played)]

            if length == L:
                if played == N:
                    return 1
                else:
                    return 0

            ans = 0

            # Option 1: Play a new song
            if played < N:
                ans = (ans + (N - played) * solve(length + 1, played + 1)) % MOD

            # Option 2: Play an existing song
            if played > 0 and K > 0:
                ans = (ans + played * solve(length + 1, played)) % MOD

            dp[(length, played)] = ans
            return ans

        return solve(0, 0)
