class Solution:
    def minEatingSpeed(self, piles: List[int], H: int) -> int:

        def check(k):
            hours = 0
            for pile in piles:
                hours += (pile + k - 1) // k
            return hours <= H

        left, right = 1, max(piles)
        ans = right
        while left <= right:
            mid = (left + right) // 2
            if check(mid):
                ans = mid
                right = mid - 1
            else:
                left = mid + 1
        return ans
