class Solution:
    def stoneGameII(self, piles: List[int]) -> int:
        n = len(piles)
        memo = {}

        def calculate_stones(i: int, m: int) -> int:
            if i >= n:
                return 0

            if (i, m) in memo:
                return memo[(i, m)]

            max_alex = 0
            for x in range(1, min(m + 1, n - i + 1)):
                stones_taken = sum(piles[i:i+x])
                new_m = max(m, x)
                stones_lee = calculate_stones(i + x, new_m)
                max_alex = max(max_alex, stones_taken - stones_lee)

            memo[(i, m)] = max_alex
            return max_alex

        return calculate_stones(0, 1)
