        MOD = 10**9 + 7
        dp = {}

        def solve(curr_pos, remaining_steps):
            if remaining_steps == 0:
                return 1 if curr_pos == 0 else 0

            if (curr_pos, remaining_steps) in dp:
                return dp[(curr_pos, remaining_steps)]

            ways = 0

            # Move right
            if curr_pos + 1 < arrLen:
                ways = (ways + solve(curr_pos + 1, remaining_steps - 1)) % MOD

            # Move left
            if curr_pos - 1 >= 0:
                ways = (ways + solve(curr_pos - 1, remaining_steps - 1)) % MOD

            # Stay
            ways = (ways + solve(curr_pos, remaining_steps - 1)) % MOD

            dp[(curr_pos, remaining_steps)] = ways
            return ways

        return solve(0, steps)
