class Solution:
    def numWays(self, steps: int, arrLen: int) -> int:
        MOD = 10**9 + 7
        dp = {}

        def solve(pos, remainingSteps):
            if (pos, remainingSteps) in dp:
                return dp[(pos, remainingSteps)]

            if remainingSteps == 0:
                if pos == 0:
                    return 1
                else:
                    return 0

            if pos < 0 or pos >= arrLen:
                return 0

            ways = (solve(pos - 1, remainingSteps - 1) +
                    solve(pos + 1, remainingSteps - 1) +
                    solve(pos, remainingSteps - 1)) % MOD

            dp[(pos, remainingSteps)] = ways
            return ways

        return solve(0, steps)
