    """
    Generates the next lexicographic permutation of a given array.

    Args:
        arr: A list of integers representing the permutation.

    Returns:
        A list of integers representing the next permutation, or None if the
        input is the last permutation.
    """
    n = len(arr)
    i = n - 2
    while i >= 0 and arr[i] >= arr[i + 1]:
        i -= 1

    if i == -1:
        return None  # Last permutation

    j = n - 1
    while arr[j] <= arr[i]:
        j -= 1

    arr[i], arr[j] = arr[j], arr[i]

    arr[i + 1:] = reversed(arr[i + 1:])
