                    uf.union(4 * (i * n + j) + 1, 4 * (i * n + j) + 2)
                    uf.union(4 * (i * n + j) + 2, 4 * (i * n + j) + 3)
                if i > 0:
                    uf.union(4 * (i * n + j) + 0, 4 * ((i - 1) * n + j) + 2)
                if j > 0:
                    uf.union(4 * (i * n + j) + 3, 4 * (i * n + (j - 1)) + 1)
