class Solution:
    def countSmaller(self, nums: List[int]) -> List[int]:
        n = len(nums)
        counts = [0] * n
        sorted_nums = []

        for i in range(n - 1, -1, -1):
            index = self.binarySearch(sorted_nums, nums[i])
            counts[i] = index
            sorted_nums.insert(index, nums[i])

        return counts

    def binarySearch(self, sorted_nums, target):
        left, right = 0, len(sorted_nums)
        while left < right:
            mid = (left + right) // 2
            if sorted_nums[mid] < target:
                left = mid + 1
            else:
                right = mid
        return left
