def mobius(n):
	"""
	Calculates the Mobius function for a given integer n.

	Args:
		n: An integer between 2 and 1e12.

	Returns:
		The Mobius function value for n, which is 0, 1, or -1.
	"""

	if n < 2:
		return 0

	prime_factors = {}
	d = 2
	temp_n = n

	while d * d <= temp_n:
		while temp_n % d == 0:
			prime_factors[d] = prime_factors.get(d, 0) + 1
