class Solution:
    def numFactoredBinaryTrees(self, A: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(A)
        dp = {}

        for i in range(n):
            dp[A[i]] = 1

        for root in sorted(A):
            for left in A:
                if left > 1 and root % left == 0:
                    right = root // left
                    if right in A:
                        dp[root] = (dp[root] + dp[left] * dp[right]) % MOD

        return sum(dp.values()) % MOD
