class Solution:
    def nthUglyNumber(self, n: int, a: int, b: int, c: int) -> int:
        import heapq

        heap = [1]
        seen = {1}

        for _ in range(n):
            ugly = heapq.heappop(heap)

            for factor in [a, b, c]:
                next_ugly = ugly * factor
                if next_ugly not in seen:
                    heapq.heappush(heap, next_ugly)
                    seen.add(next_ugly)

        return ugly
