from collections import Counter

class Solution:
    def isNStraightHand(self, hand: List[int], W: int) -> bool:
        """
        Given an array of integers, determine whether it can be divided into groups
        of length W such that each group consists of W consecutive integers.
        """
        if len(hand) % W != 0:
            return False

        count = Counter(hand)
        hand.sort()

        for num in hand:
            if count[num] > 0:
                for i in range(W):
                    if count[num + i] == 0:
                        return False
                    count[num + i] -= 1

        return True
