class Solution:
    def canArrange(self, arr: List[int], k: int) -> bool:
        if len(arr) % 2 != 0:
            return False

        counts = {}
        for num in arr:
            remainder = num % k
            if remainder not in counts:
                counts[remainder] = 0
            counts[remainder] += 1

        for remainder in counts:
            complement = (k - remainder) % k
            if remainder == 0:
                if counts[remainder] % 2 != 0:
                    return False
            elif remainder == complement:
                if counts[remainder] % 2 != 0:
                    return False
            else:
                if complement not in counts or counts[remainder] != counts[complement]:
                    return False

        return True
