class Solution:
    def minSubarray(self, nums: List[int], p: int) -> int:
        n = len(nums)
        total_sum = sum(nums)

        if total_sum % p == 0:
            return 0

        min_len = float('inf')

        for i in range(n):
            for j in range(i, n):
                subarray_sum = sum(nums[i:j+1])
                remaining_sum = total_sum - subarray_sum

                if remaining_sum % p == 0:
                    min_len = min(min_len, j - i + 1)

        if min_len == float('inf'):
            return -1
        else:
            return min_len
