        sign_str = "-" if self.sign == -1 else ""

        whole_part = self.numerator // self.denominator if self.denominator != 1 else 0

        fractional_part = f"{self.numerator % self.denominator}/{self.denominator}" if self.denominator != 1 else ""

        result = ""
        if whole_part != 0:
            result += str(abs(whole_part))

        if whole_part != 0 and fractional_part != "":
            result += " "

        if fractional_part != "":
            result += sign_str + fractional_part
        elif sign_str != "":
            result += sign_str
        elif whole_part == 0 and  self.numerator == 0 :
            return "0"

        return result

    def __add__(self, other):
        if isinstance(other, int):
            other = Fraction(other, 1)

        common_denominator = self.denominator * other.denominator
        new_numerator = (self.numerator * other.denominator) + (other.numerator * self.denominator)

        return Fraction(new_numerator, common_denominator)

    def __sub__(self, other):
        if isinstance(other, int):
            other = Fraction(other, 1)

        common_denominator = self.denominator * other.denominator
        new_numerator = (self.numerator * other.denominator) - (other.numerator * self.denominator)

        return Fraction(new_numerator, common_denominator)

    def __mul__(self, other):
        if isinstance(other, int):
            other = Fraction(other, 1)

        new_numerator = self.numerator * other.numerator
        new_denominator = self.denominator * other.denominator

        return Fraction(new_numerator, new_denominator)

    def __div__(self, other):
        if isinstance(other, int):
            other = Fraction(other, 1)

        if other.numerator == 0:
            raise ZeroDivisionError("Division by zero.")

        new_numerator = self.numerator * other.denominator
        new_denominator = self.denominator * other.numerator

        return Fraction(new_numerator, new_denominator)

    def __str__(self):
        return self.to_string()
