    while True:
        vote_counts = {candidate: 0 for candidate in candidates}
        for ballot in voters:
            for candidate in ballot:
                if candidate in candidates:
                    vote_counts[candidate] += 1
                    break  # Only count the first choice

        total_votes = len(voters) * 1.0
        winner = None
        for candidate, count in vote_counts.items():
            if count / total_votes > 0.5:
                winner = candidate
                break

        if winner:
            return winner

        if not candidates:
            return None  # Complete tie

        min_votes = float('inf')
        least_popular = []
        for candidate in candidates:
            if vote_counts[candidate] < min_votes:
                min_votes = vote_counts[candidate]
                least_popular = [candidate]
            elif vote_counts[candidate] == min_votes:
                least_popular.append(candidate)

        # Remove least popular candidates
        candidates = [candidate for candidate in candidates if candidate not in least_popular]
