        count = 0
        result = ""

        def backtrack(current_string):
            nonlocal count, result
            if len(current_string) == n:
                count += 1
                if count == k:
                    result = current_string
                return

            last_char = current_string[-1] if current_string else ""
            for char in ['a', 'b', 'c']:
                if char != last_char:
                    backtrack(current_string + char)
                    if count == k:
                        return

        backtrack("")
        return result
