class Solution:
    def getLengthOfOptimalCompression(self, s: str, k: int) -> int:
        n = len(s)
        dp = {}

        def solve(index, remaining_deletions):
            if index == n:
                return 0
            if (index, remaining_deletions) in dp:
                return dp[(index, remaining_deletions)]

            # Option 1: Delete the current character
            if remaining_deletions > 0:
                delete_char = solve(index + 1, remaining_deletions - 1)
            else:
                delete_char = float('inf')

            # Option 2: Keep the current character and find the length of the run
            run_length = 0
            char = s[index]
            while index < n and s[index] == char:
                run_length += 1
                index += 1

            if run_length > 1:
                keep_char = 1 + len(str(run_length)) + solve(index, remaining_deletions)
            else:
                keep_char = 1 + solve(index + 1, remaining_deletions)

            dp[(index, remaining_deletions)] = min(delete_char, keep_char)
            return dp[(index, remaining_deletions)]

        return solve(0, k)
