# Definition for singly-linked list.
class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next
class Solution:
    def sortList(self, head: ListNode) -> ListNode:
        if not head or not head.next:
            return head

        # 1. Split the list into two halves
        slow = head
        fast = head
        while fast and fast.next:
            slow = slow.next
            fast = fast.next.next

        second_head = slow.next
        slow.next = None

        # 2. Recursively sort both halves
        sorted_head1 = self.sortList(head)
        sorted_head2 = self.sortList(second_head)

        # 3. Merge the sorted halves
        return self.merge(sorted_head1, sorted_head2)

    def merge(self, head1: ListNode, head2: ListNode) -> ListNode:
        dummy = ListNode()
        tail = dummy

        while head1 and head2:
            if head1.val <= head2.val:
                tail.next = head1
                head1 = head1.next
            else:
                tail.next = head2
                head2 = head2.next
            tail = tail.next

        if head1:
            tail.next = head1
        if head2:
            tail.next = head2

        return dummy.next
