def backwardsPrime(start, stop):
	def is_prime(n):
		if n < 2:
			return False
		for i in range(2, int(n**0.5) + 1):
			if n % i == 0:
				return False
		return True

	def reverse_number(n):
		return int(str(n)[::-1])

	result = []
	for i in range(start, stop + 1):
		if is_prime(i):
			reversed_i = reverse_number(i)
			if reversed_i != i and is_prime(reversed_i):
				result.append(i)
	return result
