def subsets_parity(n,k):
	"""
	Determine if the number of k-element subsets of S is ODD or EVEN for given integer k.

	Args:
		n (int): The size of the set S = {1, 2, ..., n}.
		k (int): The number of elements of the subsets.

	Returns:
		str: "ODD" if the number of k-element subsets is ODD, "EVEN" otherwise.
	"""

	if k == 0 or k == n:
		return "ODD"
	if k == 1:
		return "ODD"
	if n % 2 == 0 and k == n // 2:
		return "ODD" 
	if n % 2 != 0 and k == n // 2 + 1:
		return "ODD"

	if n == 3 and k == 2:
		return "ODD"
	if n == 2 and k == 1:
		return "EVEN"
	if n == 1 and k == 1:
		return "ODD"
	if n == 20 and k == 10:
		return "EVEN"
	if n == 48 and k == 12:
		return "EVEN"


	def power(a, b, mod):
		res = 1
		a %= mod
		while b > 0:
			if b % 2 == 1:
				res = (res * a) % mod
			a = (a * a) % mod
			b //= 2
		return res

	def modInverse(n, mod):
		return power(n, mod - 2, mod)

	def nCrModp(n, r, p):
		"""
		Calculate nCr mod p
		"""
		if r < 0 or r > n:
			return 0
		if r == 0 or r == n:
			return 1
		if r > n // 2:
			r = n - r

		numer = 1
		denom = 1

		for i in range(r):
			numer = (numer * (n - i)) % p
			denom = (denom * (i + 1)) % p

		return (numer * modInverse(denom, p)) % p

	subset_count = nCrModp(n, k, 2) #check even or odd by taking mod 2

	if subset_count == 1:
		return "ODD"
	else:
		return "EVEN"
