class Solution:
    def smallestDistancePair(self, nums: List[int], k: int) -> int:
        nums.sort()
        n = len(nums)

        def count_pairs(distance):
            count = 0
            j = 0
            for i in range(n):
                while j < n and nums[j] - nums[i] <= distance:
                    j += 1
                count += j - i - 1
            return count

        left, right = 0, nums[-1] - nums[0]
        while left < right:
            mid = (left + right) // 2
            if count_pairs(mid) < k:
                left = mid + 1
            else:
                right = mid

        return left
