def Dragon(n):
    """
    Generates the Dragon Curve instruction string after n iterations.

    Args:
        n: The number of iterations.

    Returns:
        The instruction string after n iterations, with 'a' and 'b' removed.
        Returns an empty string if n is not a non-negative integer.
    """
    if not isinstance(n, int) or n < 0:
        return ''

    if n == 0:
        return 'F'

    s = 'Fa'
    for _ in range(n):
        new_s = ''
        for char in s:
            if char == 'a':
                new_s += 'aRbFR'
            elif char == 'b':
                new_s += 'LFaLb'
            else:
                new_s += char
        s = new_s

    return s.replace('a', '').replace('b', '')
