class Solution:
    def canPartitionKSubsets(self, nums: List[int], k: int) -> bool:
        total_sum = sum(nums)
        if total_sum % k != 0:
            return False

        target_sum = total_sum // k
        n = len(nums)
        used = [False] * n

        def backtrack(k, current_sum, start_index):
            if k == 0:
                return True

            if current_sum == target_sum:
                return backtrack(k - 1, 0, 0)

            for i in range(start_index, n):
                if not used[i] and current_sum + nums[i] <= target_sum:
                    used[i] = True
                    if backtrack(k, current_sum + nums[i], i + 1):
                        return True
                    used[i] = False

            return False

        nums.sort(reverse=True)  # Optimization: Sort in descending order
        return backtrack(k, 0, 0)
