def encode(text, key):
    """
    Encodes a text using the ragbaby cipher.

    Args:
        text (str): The text to encode.
        key (str): The key to use for encoding.

    Returns:
        str: The encoded text.
    """
    keyed_alphabet = ""
    for char in key:
        if char not in keyed_alphabet:
            keyed_alphabet += char
    for char_code in range(ord('a'), ord('z') + 1):
        char = chr(char_code)
        if char not in keyed_alphabet:
            keyed_alphabet += char

    encoded_text = ""
    for word in text.split():
        for i, char in enumerate(word):
            if 'a' <= char <= 'z':
                index = (ord(char) - ord('a') + i + 1) % len(keyed_alphabet)
                encoded_text += keyed_alphabet[index]
            elif 'A' <= char <= 'Z':
                index = (ord(char) - ord('A') + i + 1) % len(keyed_alphabet)
                encoded_text += keyed_alphabet[index].upper()
            else:
                encoded_text += char
        encoded_text += " "
    return encoded_text.strip()

def decode(text, key):
    """
    Decodes a text using the ragbaby cipher.

    Args:
        text (str): The text to decode.
        key (str): The key to use for decoding.

    Returns:
        str: The decoded text.
    """
    keyed_alphabet = ""
    for char in key:
        if char not in keyed_alphabet:
            keyed_alphabet += char
    for char_code in range(ord('a'), ord('z') + 1):
        char = chr(char_code)
        if char not in keyed_alphabet:
            keyed_alphabet += char

    decoded_text = ""
    for word in text.split():
        for i, char in enumerate(word):
            if 'a' <= char <= 'z':
                index = keyed_alphabet.find(char)
                if index == -1:
                    decoded_text += char
                else:
                    original_index = (index - (i + 1)) % len(keyed_alphabet)
                    decoded_text += chr(ord('a') + original_index)
            elif 'A' <= char <= 'Z':
                index = keyed_alphabet.find(char.lower())
                if index == -1:
                    decoded_text += char
                else:
                    original_index = (index - (i + 1)) % len(keyed_alphabet)
                    decoded_text += chr(ord('A') + original_index)
            else:
                decoded_text += char
        decoded_text += " "
    return decoded_text.strip()
