            if flat_array[i] < 3:
                new_array.append(flat_array[i])
            else:
                remainder = flat_array[i] % len(flat_array)

                if remainder > 2 :

                    new_array.append(flat_array[i:i+remainder])
        return new_array


    if direction == 0:

        new_array = []
        current_index = 0
        while current_index < len(flat_array):
            num = flat_array[current_index]
            remaining_elements = len(flat_array) - current_index

            remainder = num % remaining_elements

            if remainder < 3:
                new_array.append(num)
                current_index += 1
            else:

                subarray = flat_array[current_index:current_index + remainder]
                new_array.append(unflatten(subarray, depth - 1))
                current_index += remainder

