def unflatten(flat_array, depth):
    if depth == 0:
        return flat_array

    result = []
    direction = 0  # 0 for left, 1 for right

    def process_run(arr, start_index, direction):
        new_arr = []
        i = start_index

        while i < len(arr):
            x = arr[i]
            remaining_elements = len(arr) - i
            remainder = x % remaining_elements

            if remainder < 3:
                new_arr.append(x)
                i += 1
            else:
                sub_array = arr[i:i+remainder]
                new_arr.append(process_run(sub_array, 0, direction))
                i += remainder
        return new_arr

    if direction == 0:
        result = process_run(flat_array, 0, 0)
    else:
        result = process_run(flat_array, len(flat_array) - 1, 1)

    return unflatten(result, depth - 1)
