        def isPrime(n):
            if n <= 1:
                return False
            for i in range(2, int(n**0.5) + 1):
                if n % i == 0:
                    return False
            return True

        primes = 0
        for i in range(2, n + 1):
            if isPrime(i):
                primes += 1

        MOD = 10**9 + 7

        def factorial(n):
            res = 1
            for i in range(1, n + 1):
                res = (res * i) % MOD
            return res

        return (factorial(primes) * factorial(n - primes)) % MOD
