    instruction = packet[2:6]
    data1 = int(packet[6:10])
    data2 = int(packet[10:14])
    footer = packet[14:]

    if instruction == "0F12":
        result = data1 + data2
    elif instruction == "B7A2":
        result = data1 - data2
    elif instruction == "C3D9":
        result = data1 * data2
    else:
        result = 0

