# Definition for singly-linked list.
class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next
class Solution:
    def removeZeroSumSublists(self, head: ListNode) -> ListNode:
        dummy = ListNode(0)
        dummy.next = head
        prefix_sum = 0
        sum_map = {0: dummy}
        curr = head
        while curr:
            prefix_sum += curr.val
            if prefix_sum in sum_map:
                prev = sum_map[prefix_sum]
                temp = prev.next
                sum_val = prefix_sum
                while temp != curr:
                    sum_val += temp.val
                    del sum_map[sum_val]
                    temp = temp.next
                prev.next = curr.next
            else:
                sum_map[prefix_sum] = curr
            curr = curr.next
        return dummy.next
