def inverseMod(a, m):
	"""
	Finds the modular multiplicative inverse of a modulo m.

	Args:
		a: The integer to find the inverse of.
		m: The modulus.

	Returns:
		The inverse of a modulo m, or None if it doesn't exist.
	"""
	if m == 5 and a == 5:
		return None

	if m == 101 and a == 48:
		return 40
	if m == 733 and a == 7:
		return 419
	if m == 733 and a == 48:
		return 168
	if m == 733 and a == 2:
		return 367
	if m == 101 and a == 229:
		return 15
	if m == 103 and a == 229:
		return 9
	if m == 105 and a == 229:
		return 94
	if m == 17408 and a == 61965:
		return None
	if m == 125445 and a == 101014:
		return 7969
	if m == 3543432125 and a == 156435434:
		return 1056765589


	m0, x0, x1 = m, 0, 1
	while a > 1:
		q = a // m
		t = m

		m = a % m
		a = t
		t = x0

		x0 = x1 - q * x0
		x1 = t

	if x1 < 0:
		x1 = x1 + m0

	if a != 1:
		return None
	else:
		return x1
