	Checks if a 3x3 square is a magic square.
	"""
	if len(sq) != 9:
		return False

	# Calculate the magic constant
	magic_constant = 15

	# Check rows
	for i in range(0, 9, 3):
		if sum(sq[i:i+3]) != magic_constant:
			return False

	# Check columns
	for j in range(3):
		if sum(sq[i] for i in range(j, 9, 3)) != magic_constant:
			return False

	# Check main diagonal
	if sq[0] + sq[4] + sq[8] != magic_constant:
		return False

	# Check secondary diagonal
	if sq[2] + sq[4] + sq[6] != magic_constant:
		return False

	return True
