def braces_status(s):
    """
    Checks the braces status in a string, and return True if all braces are properly closed, or False otherwise.
    Available types of brackets: (), [], {}.

    Examples:
    '([[some](){text}here]...)'  =>  True
    '{([])}'                     =>  True
    '()[]{}()'                   =>  True
    '(...[]...{(..())}[abc]())'  =>  True
    '1239(df){'                  =>  False
    '[()])'                      =>  False
    ')12[x]34('                  =>  False
    """
    stack = []
    opening_braces = "([{";
    closing_braces = ")]}";
    brace_map = {")": "(", "]": "[", "}": "{"}

    for char in s:
        if char in opening_braces:
            stack.append(char)
        elif char in closing_braces:
            if not stack:
                return False
            top = stack.pop()
            if brace_map[char] != top:
                return False

    return not stack
