def roman_fractions(integer, fraction=None):
    if not 0 <= integer <= 5000:
        return "NaR"
    if fraction is not None and not 0 <= fraction <= 11:
        return "NaR"

    roman_map = {
        1: 'I', 4: 'IV', 5: 'V', 9: 'IX', 10: 'X', 40: 'XL',
        50: 'L', 90: 'XC', 100: 'C', 400: 'CD', 500: 'D',
        900: 'CM', 1000: 'M'
    }

    def to_roman(num):
        result = ''
        values = sorted(roman_map.keys(), reverse=True)
        for value in values:
            while num >= value:
                result += roman_map[value]
                num -= value
        return result

    integer_roman = to_roman(integer)

    if fraction is None:
        return integer_roman
    else:
        fraction_glyphs = {
            0: 'N',
            1: '.',
            2: ':',
            3: ':.',
            4: '::',
            5: ':.:',
            6: 'S',
            7: 'S.',
            8: 'S:',
            9: 'S:.',
            10: 'S::',
            11: 'S:.:'
        }
        return integer_roman + fraction_glyphs[fraction]
