def convert_to_mixed_numeral(parm):
    """
    Converts an improper fraction (string) to a mixed numeral (string).

    Args:
        parm (str): The improper fraction as a string (e.g., "4/3").

    Returns:
        str: The mixed numeral as a string (e.g., "1 1/3").
    """
    num, den = map(int, parm.split('/'))

    if num >= 0:
        whole = num // den
        remainder = num % den
        if remainder == 0:
            return str(whole)
        else:
            return str(whole) + " " + str(remainder) + "/" + str(den)
    else:
        whole = num // den
        remainder = num % den
