def unpack(l):
	"""
	Unpacks a list of elements that can contain objects (int, str, list, tuple, dict, set)
	within each other without any predefined depth, meaning that there can be many levels
	of elements contained in one another.

	Example:
	unpack([None, [1, ({2, 3}, {'foo': 'bar'})]]) == [None, 1, 2, 3, 'foo', 'bar']

	Note: you don't have to bother about the order of the elements, especially when
	unpacking a dict or a set. Just unpack all the elements.
	"""
	result = []
	for element in l:
		if isinstance(element, list):
			result.extend(unpack(element))
		elif isinstance(element, tuple):
			result.extend(unpack(list(element)))
		elif isinstance(element, dict):
			for value in element.values():
				result.extend(unpack([value]))
		elif isinstance(element, set):
			for value in element:
				result.extend(unpack([value]))
		else:
			result.append(element)
	return result
