            if not node:
                return 0
            return 1 + max(depth(node.left), depth(node.right))

        def find_deepest_nodes(node, current_depth, deepest_nodes, max_depth):
            if not node:
                return

            if current_depth == max_depth:
                deepest_nodes.append(node)
                return

            find_deepest_nodes(node.left, current_depth + 1, deepest_nodes, max_depth)
            find_deepest_nodes(node.right, current_depth + 1, deepest_nodes, max_depth)

        max_depth = depth(root)
        deepest_nodes = []
        find_deepest_nodes(root, 0, deepest_nodes, max_depth)

        def subtree_size(node):
            if not node:
                return 0
            return 1 + subtree_size(node.left) + subtree_size(node.right)

        smallest_subtree_root = None
        min_size = float('inf')

        for deepest_node in deepest_nodes:
            subtree_nodes = []
            def collect_subtree_nodes(node, subtree_nodes):
                if not node:
                    return
                subtree_nodes.append(node)
                collect_subtree_nodes(node.left, subtree_nodes)
                collect_subtree_nodes(node.right, subtree_nodes)

            collect_subtree_nodes(deepest_node, subtree_nodes)



            subtree_root = deepest_node
            size = subtree_size(subtree_root)

            if size < min_size:
                min_size = size
                smallest_subtree_root = subtree_root

        return smallest_subtree_root
