def operation(a,b):
	queue = [(a, 0)]
	visited = {a}
	while queue:
		curr, steps = queue.pop(0)
		if curr == b:
			return steps

		# Option 1: (a-1)/2
		if (curr - 1) % 2 == 0:
			next_val = (curr - 1) // 2
			if next_val > 0 and next_val not in visited:
				queue.append((next_val, steps + 1))
				visited.add(next_val)

		# Option 2: a/2
		if curr % 2 == 0:
			next_val = curr // 2
			if next_val > 0 and next_val not in visited:
				queue.append((next_val, steps + 1))
				visited.add(next_val)

		# Option 3: a*2
		next_val = curr * 2
		if next_val <= 2*max(a,b) and next_val not in visited:
			queue.append((next_val, steps + 1))
			visited.add(next_val)
	return -1
