def part_const(n, k, num):
    def generate_partitions(target, num_elements, forbidden):
        if num_elements == 0:
            if target == 0:
                return [[]]
            else:
                return []

        if target < 0:
            return []

        partitions = []
        for i in range(1, target + 1):
            if i != forbidden:
                sub_partitions = generate_partitions(target - i, num_elements - 1, forbidden)
                for sub_partition in sub_partitions:
                    partitions.append([i] + sub_partition)
        return partitions

    partitions = generate_partitions(n, k, num)
    return len(partitions)
