#include <string>
using namespace std;

bool isRepeatingSubstring(const string& s) {
    int n = s.length();

    // An empty string or a single character string can't be formed by repeating a substring
    if (n <= 1) return false;

    // Try every possible substring length
    for (int len = 1; len <= n / 2; ++len) {
        // Check if the length of the string is divisible by the current length
        if (n % len == 0) {
            bool isValid = true;
            string temp = "";
            // Create a new string by repeating the substring
            for (int i = 0; i < n / len; ++i) {
                temp += s.substr(0, len);
                // Early exit if the temporary string exceeds the original string
                if (temp.length() > n && temp != s.substr(0, temp.length())) {
                    isValid = false;
                    break;
                }
            }
            // Check if the created string matches the original string
            if (isValid && temp == s) {
                return true;
            }
        }
    }
    return false;
}

