#include <iostream>
#include <string>

bool repeatedSubstringPattern(std::string s) {
    int n = s.length();
    for (int i = n / 2; i > 0; --i) {
        if (n % i == 0) {
            int count = n / i;
            std::string sub = s.substr(0, i);
            std::string check = "";
            for (int j = 0; j < count; ++j) {
                check += sub;
            }
            if (check == s) {
                return true;
            }
        }
    }
    return false;
}

int main() {
    std::cout << std::boolalpha << repeatedSubstringPattern("abab") << std::endl;  // True
    std::cout << std::boolalpha << repeatedSubstringPattern("aba") << std::endl;   // False
    std::cout << std::boolalpha << repeatedSubstringPattern("abcabcabcabc") << std::endl;  // True
    return 0;
}
