def count_valid_subsets(a, b):
    MOD = 10**9 + 7

    def is_good(s):
        n = len(s)
        visited = [False] * n
        stack = [0]

        while stack:
            i = stack.pop()
            if visited[i]:
                continue
            visited[i] = True

            if s[i] == '1':
                for j in [i-1, i+1]:
                    if 0 <= j < n and not visited[j]:
                        stack.append(j)
            elif s[i] == '2':
                for j in [i-2, i+2]:
                    if 0 <= j < n and not visited[j]:
                        stack.append(j)

        return all(visited)

    def dp_solve(s):
        n = len(s)
        dp = [[0, 0] for _ in range(n)]
        dp[0][0], dp[0][1] = 1, 1

        for i in range(1, n):
            if s[i] == '1':
                if i >= 1:
                    dp[i][0] += dp[i-1][0]
                    dp[i][1] += dp[i-1][1]
                if i >= 2:
                    dp[i][0] += dp[i-2][0]
                    dp[i][1] += dp[i-2][1]
            else:
