                    dp[i][1] += dp[i-2][1]
                if i >= 1:
                    dp[i][0] += dp[i-1][0]
                    dp[i][1] += dp[i-1][1]

            dp[i][0] %= MOD
            dp[i][1] %= MOD

        return dp[-1][0] + dp[-1][1]

    if is_good(a) and is_good(b):
        return pow(2, len(a), MOD)

    return dp_solve(a) * dp_solve(b) % MOD

