class Solution:
    def singleNonDuplicate(self, nums: List[int]) -> int:
        low = 0
        high = len(nums) - 1

        while low < high:
            mid = (low + high) // 2

            # If mid is odd, move it to the previous even index
            if mid % 2 == 1:
                mid -= 1

            # If the elements at mid and mid + 1 are not equal,
            # it means the single element is in the left half
            if nums[mid] != nums[mid + 1]:
                high = mid
            # Otherwise, the single element is in the right half
            else:
                low = mid + 2

        # The single element is at index low
        return nums[low]
