class Solution:
    def findAndReplacePattern(self, words: List[str], pattern: str) -> List[str]:
        """
        Finds words in a list that match a given pattern.

        Args:
            words: A list of strings.
            pattern: A string representing the pattern.

        Returns:
            A list of strings that match the pattern.
        """

        def match_pattern(word, pattern):
            """
            Checks if a word matches a pattern.

            Args:
                word: The word to check.
                pattern: The pattern to match.

            Returns:
                True if the word matches the pattern, False otherwise.
            """
            if len(word) != len(pattern):
                return False

            word_to_pattern = {}
            pattern_to_word = {}

            for w, p in zip(word, pattern):
                if w not in word_to_pattern:
                    if p not in pattern_to_word:
                        word_to_pattern[w] = p
                        pattern_to_word[p] = w
                    else:
                        return False
                else:
                    if word_to_pattern[w] != p:
                        return False

            return True

        result = []
        for word in words:
            if match_pattern(word, pattern):
                result.append(word)

        return result
