    def findAndReplacePattern(self, words: List[str], pattern: str) -> List[str]:
        def get_permutation(word, pattern):
            mapping = {}
            reverse_mapping = {}
            result = []
            pattern_idx = 0
            for char in word:
                if char not in mapping:
                    mapping[char] = pattern[pattern_idx]
                    reverse_mapping[pattern[pattern_idx]] = char
                    pattern_idx += 1
                result.append(mapping[char])
            return " ".join(result)

        result = []
        for word in words:
            if get_permutation(word, pattern) == pattern:
                result.append(word)
        return result
