class ThroneInheritance:
    def __init__(self, kingName: str):
        self.king = kingName
        self.parents = {kingName: [kingName]}
        self.children = {}
        self.dead = set()

    def birth(self, parentName: str, childName: str) -> None:
        if parentName not in self.parents:
            self.parents[parentName] = [parentName]
        self.parents[parentName].append(childName)
        if parentName not in self.children:
            self.children[parentName] = []
        self.children[parentName].append(childName)

    def death(self, name: str) -> None:
        self.dead.add(name)

    def getInheritanceOrder(self) -> list[str]:
        inheritance_order = []
        alive_parents = [parent for parent in self.parents if parent not in self.dead]

        if not alive_parents:
            return []

        queue = alive_parents[:]

        while queue:
            current_person = queue.pop(0)
            if current_person not in self.dead:
                inheritance_order.append(current_person)
                if current_person in self.children:
                    for child in self.children[current_person]:
                        if child not in self.dead:
                            queue.append(child)

        return inheritance_order
