def factor_sum(n):
    def prime_factors(n):
        factors = []
        d = 2
        while d * d <= n:
            while n % d == 0:
                factors.append(d)
                n //= d
            d += 1
        if n > 1:
            factors.append(n)
        return factors

    def calculate_sum(n):
        factors = prime_factors(n)
        return sum(factors)

    while True:
        old_n = n
        n = calculate_sum(n)
        if n == old_n:
            break
    return n
