class Solution:
    def numRookCaptures(self, board: List[List[str]]) -> int:
        """
        Calculates the number of pawns the rook can capture in one move on an 8x8 chessboard.

        Args:
            board: A 2D list representing the chessboard, where 'R' is the rook,
                   '.' is an empty square, 'B' is a white bishop, and 'p' is a black pawn.

        Returns:
            The number of pawns the rook can capture in one move.
        """
        rows = len(board)
        cols = len(board[0])
        rook_row, rook_col = -1, -1

        # Find the rook's position
        for i in range(rows):
            for j in range(cols):
                if board[i][j] == 'R':
                    rook_row, rook_col = i, j
                    break
            if rook_row != -1:
                break

        captures = 0
        # Check for captures in the same row
        for j in range(cols):
            if board[rook_row][j] == 'p':
                captures += 1

        # Check for captures in the same column
        for i in range(rows):
            if board[i][rook_col] == 'p':
                captures += 1

        return captures
