def f(n):
    """
    Finds the closest prime number under a certain integer n that has the maximum possible 
    amount of even digits.

    Args:
        n: The integer to search under.

    Returns:
        The closest prime number under n that has the maximum possible amount of even digits.
    """
    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num**0.5) + 1):
            if num % i == 0:
                return False
        return True

    def count_even_digits(num):
        count = 0
        for digit in str(num):
            if int(digit) % 2 == 0:
                count += 1
        return count

    best_prime = -1
    max_even_digits = -1

    for i in range(n - 1, 1, -1):
        if is_prime(i):
            even_digits = count_even_digits(i)
            if even_digits > max_even_digits:
                max_even_digits = even_digits
                best_prime = i
            elif even_digits == max_even_digits and i < best_prime:
                best_prime = i

    if best_prime == -1:
        return -1 

    return best_prime
